import './App.css';

import React, {useState} from 'react';
import axios from 'axios';


// Style for centering and customizing the look
const styles = {
  container: {
    textAlign: 'center',
    fontFamily: 'Arial, sans-serif',
    marginTop: '50px',
  },
  heading: {
    fontSize: '36px',
    fontWeight: 'bold',
    color: '#1d3b73', // Custom color for the heading
    fontFamily: 'Georgia, serif', // A nice serif font for the team name
  },
  subHeading: {
    fontSize: '24px',
    color: '#555',
    marginTop: '10px',
  },
  searchContainer: {
    marginTop: '20px',
  },
  input: {
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    width: '300px',
  },
  button: {
    marginLeft: '10px',
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#007BFF',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  results: {
    marginTop: '20px',
    fontSize: '18px',
    fontWeight: 'bold',
  },
};

function App() {

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [playerStats, setPlayerStats] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/api/matching_players?first_name=${firstName}&last_name=${lastName}`);
      setPlayerStats(response.data);
    } catch (error) {
      console.error('Error fetching player data:', error);
      setPlayerStats(null);
    }
  };



  return (
    <div style={styles.container}>

      {/* Headings */}
      <div style={styles.heading}>Kansas City Royals</div>
      <div style={styles.subHeading}>Player Stat Lookup</div>


      {/* Search Bar */}
      <div style={styles.searchContainer}>
        <input
          type="text"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          placeholder="First Name"
          style={styles.input}
        />
        <input
          type="text"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          placeholder="Last Name"
          style={styles.input}
        />
        <button onClick={handleSearch} style={styles.button}>Search</button>
      </div>


      {/* Search Results */}
      {playerStats && (
        <div style={styles.results}>
          <h3>Player Stats:</h3>
          {playerStats.length > 0 ? (
            <ul style={{ listStyle: "none", padding: 0 }}>
              {playerStats.map((player, index) => (
                <li key={index} style={{ marginBottom: "20px", paddingBottom: "10px", borderBottom: "2px solid #ccc" }}>

                  {/* Player Name Header */}
                  <div style={{ fontSize: "20px", fontWeight: "bold", textAlign: "left" }}>
                    {player.first_name} {player.last_name}
                  </div>
                  <div style={{ fontSize: "16px", color: "#666", textAlign: "left" }}>
                    ({player.name_use})
                  </div>

                  {/* Horizontal Divider */}
                  <hr style={{ border: "1px solid #aaa", margin: "10px 0" }} />

                  {/* Two-Column Layout */}
                  <div style={{ display: "flex", justifyContent: "space-between" }}>

                    {/* Left Column */}
                    <div style={{ flex: 1, textAlign: "left" }}>
                      <p><strong>Team:</strong> {player.team}</p>
                      <p><strong>Primary Position:</strong> {player.primary_position}</p>
                      <p><strong>Throws:</strong> {player.throws}</p>
                      <p><strong>Bats:</strong> {player.bats}</p>
                    </div>

                    {/* Right Column */}
                    <div style={{ flex: 1, textAlign: "left" }}>
                      <p><strong>Height:</strong> {player.height_feet}ft {player.height_inches}in</p>
                      <p><strong>Weight:</strong> {player.weight} lbs</p>
                      <p><strong>Date of Birth:</strong> {player.birth_date}</p>
                    </div>

                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>No matching players found.</p>
          )}
        </div>
      )}
    </div>
  );
}

// Then do some line in between each one and then view baseball stats and pitching stats
//Also have Each player left aligned, then their stats, then buttons to view their batting and pitching stats

export default App;
