import './App.css';

import React, {useState} from 'react';
import axios from 'axios';


// Style for centering and customizing the look
const styles = {
  container: {
    textAlign: 'center',
    fontFamily: 'Arial, sans-serif',
    marginTop: '50px',
  },
  heading: {
    fontSize: '36px',
    fontWeight: 'bold',
    color: '#1d3b73', // Custom color for the heading
    fontFamily: 'Georgia, serif', // A nice serif font for the team name
  },
  subHeading: {
    fontSize: '24px',
    color: '#555',
    marginTop: '10px',
  },
  searchContainer: {
    marginTop: '20px',
  },
  input: {
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    width: '300px',
  },
  button: {
    marginLeft: '10px',
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#007BFF',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  results: {
    marginTop: '20px',
    fontSize: '18px',
    fontWeight: 'bold',
  },
};

function App() {

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [playerStats, setPlayerStats] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/api/matching_players?first_name=${firstName}&last_name=${lastName}`);
      setPlayerStats(response.data);
    } catch (error) {
      console.error('Error fetching player data:', error);
      setPlayerStats(null);
    }
  };



  /* Modals */
  //Can probably get rid of modal content

  const [modalOpen, setModalOpen] = useState(false);
  const [modalContent, setModalContent] = useState(null);


  // Open Modal with Player Stats

  //So instead of setting content in here, you can set BattingStats or PlayerStats equal to a request that gets the data.
  //Set that variable above. Then you can just use that variable in the model
    //const [currentBattingStats, setCurrentBattingStats] = useState([]);
    //const [currentPitchingStats, setCurrentPitchingStats] = useState([]);
    //const [currentPlayerName]




  const openModal = async (player, type) => {
    var response = null;
    console.log(player.id)
    try {
      if(type == "Pitching Stats"){
        response = await axios.get(`http://localhost:8000/api/pitching_stats?player_id=${player.id}`);
        console.log(response);
      }else if (type == "Batting Stats"){
        response = await axios.get(`http://localhost:8000/api/batting_stats?player_id=${player.id}`);
        console.log(response);
      }
    } catch (error) {
      console.error(`Error fetching ${type} data:`, error);
    }

    setModalContent({
      title: `${player.first_name} ${player.last_name} - ${type}`,
      type: type,
      stats: response
    });
    setModalOpen(true);
  };

  // Close Modal
  const closeModal = () => {
    setModalOpen(false);
    setModalContent(null);
  };




  return (
    <div style={styles.container}>

      {/* Headings */}
      <div style={styles.heading}>Kansas City Royals</div>
      <div style={styles.subHeading}>Player Stat Lookup</div>


      {/* Search Bar */}
      <div style={styles.searchContainer}>
        <input
          type="text"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          placeholder="First Name"
          style={styles.input}
        />
        <input
          type="text"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          placeholder="Last Name"
          style={styles.input}
        />
        <button onClick={handleSearch} style={styles.button}>Search</button>
      </div>


      {/* Search Results */}
      {playerStats && (
        <div style={styles.results}>
          <h3>Player Stats:</h3>
          {playerStats.length > 0 ? (
            <div style={{ display: "flex", justifyContent: "center" }}>
              <div style={{ width: "50%", minWidth: "400px" }}>
                <ul style={{ listStyle: "none", padding: 0 }}>
                  {playerStats.map((player, index) => (
                    <li 
                      key={index} 
                      style={{ 
                        marginBottom: "20px", 
                        paddingBottom: "10px",
                        borderBottom: "2px solid #ccc"
                      }}
                    >

                      {/* Player Name Header - Centered & Blue */}
                      <div style={{ 
                        fontSize: "22px", 
                        fontWeight: "bold", 
                        textAlign: "center", 
                        color: "#004687"  // Royals Blue
                      }}>
                        {player.first_name} {player.last_name}
                      </div>
                      <div style={{ fontSize: "16px", color: "#666", textAlign: "center" }}>
                        ({player.name_use})
                      </div>

                      {/* Horizontal Divider */}
                      <hr style={{ border: "1px solid #aaa", margin: "10px 0" }} />

                      {/* Three-Column Layout */}
                      <div style={{ 
                        display: "flex", 
                        justifyContent: "space-between", 
                        alignItems: "center", 
                        textAlign: "center"
                      }}>

                        {/* Left Column - Player Info */}
                        <div style={{ flex: 1 }}>
                          <p><strong>Team:</strong> {player.team}</p>
                          <p><strong>Primary Position:</strong> {player.primary_position}</p>
                          <p><strong>Throws:</strong> {player.throws}</p>
                          <p><strong>Bats:</strong> {player.bats}</p>
                        </div>

                        {/* Middle Column - Physical Stats */}
                        <div style={{ flex: 1 }}>
                          <p><strong>Height:</strong> {player.height_feet}ft {player.height_inches}in</p>
                          <p><strong>Weight:</strong> {player.weight} lbs</p>
                          <p><strong>Date of Birth:</strong> {player.birth_date}</p>
                        </div>

                        {/* Right Column - Action Buttons */}
                        <div style={{ flex: 1 }}>
                          <button 
                            style={{
                              backgroundColor: "#004687", 
                              color: "white", 
                              border: "none", 
                              padding: "8px 12px",
                              marginBottom: "8px",
                              borderRadius: "5px",
                              cursor: "pointer",
                              fontWeight: "bold"
                            }}
                            onClick={() => openModal(player, "Pitching Stats")}
                          >
                            Pitching Stats
                          </button>
                          <br />
                          <button 
                            style={{
                              backgroundColor: "#004687", 
                              color: "white", 
                              border: "none", 
                              padding: "8px 12px",
                              borderRadius: "5px",
                              cursor: "pointer",
                              fontWeight: "bold"
                            }}
                            onClick={() => openModal(player, "Batting Stats")}
                          >
                            Batting Stats
                          </button>
                        </div>

                      </div>
                    </li>
                  ))}
                </ul>
              </div>
            </div>

          ) : (
            <p>No matching players found.</p>
          )}
        </div>
      )}





      {modalOpen && (
        <div style={{
          position: "fixed",
          top: 0, left: 0, width: "100%", height: "100%",
          backgroundColor: "rgba(0,0,0,0.5)",
          display: "flex", justifyContent: "center", alignItems: "center"
        }}>
          <div style={{
            backgroundColor: "white",
            padding: "20px",
            borderRadius: "10px",
            width: "40%",
            textAlign: "center",
            boxShadow: "0px 0px 10px rgba(0,0,0,0.3)"
          }}>
            <h2 style={{ color: "#004687" }}>{modalContent.title}</h2>

            {modalContent.type == "Pitching Stats" && modalContent.stats && modalContent.stats.length > 0 ? (
              <ul style={{ listStyle: "none", padding: 0 }}>
                {modalContent.stats.map((pitchStat, index) => (
                  <li 
                    key={index} 
                    style={{ 
                      marginBottom: "20px", 
                      paddingBottom: "10px",
                      borderBottom: "2px solid #ccc"
                    }}
                  >

                    {/* Player Name Header - Centered & Blue */}
                    <div style={{ 
                      fontSize: "22px", 
                      fontWeight: "bold", 
                      textAlign: "center", 
                      color: "#004687"  // Royals Blue
                    }}>
                      {pitchStat.year} 
                    </div>


                    {/* Horizontal Divider */}
                    <hr style={{ border: "1px solid #aaa", margin: "10px 0" }} />

                    {/* Three-Column Layout */}
                    <div style={{ 
                      display: "flex", 
                      justifyContent: "space-between", 
                      alignItems: "center", 
                      textAlign: "center"
                    }}>

                      {/* Left Column - Player Info */}
                      <div style={{ flex: 1 }}>
                        <p><strong>Team:</strong> </p>
                        <p><strong>Primary Position:</strong> </p>
                        <p><strong>Throws:</strong> </p>
                        <p><strong>Bats:</strong> </p>
                      </div>

                      {/* Middle Column - Physical Stats */}
                      <div style={{ flex: 1 }}>
                        <p><strong>Height:</strong> </p>
                        <p><strong>Weight:</strong> </p>
                        <p><strong>Date of Birth:</strong> </p>
                      </div>

                      {/* Right Column - Action Buttons */}
                      <div style={{ flex: 1 }}>
                        hi
                      </div>

                    </div>
                  </li>
                ))}
              </ul>
            ) : modalContent.type == "Batting Stats" && modalContent.stats && modalContent.stats.length > 0 ? (
              <p>Batting Stats</p>
            ) : (
              <p>No stats available.</p>
            )}

            <button 
              style={{
                marginTop: "15px",
                backgroundColor: "#004687",
                color: "white",
                border: "none",
                padding: "8px 12px",
                borderRadius: "5px",
                cursor: "pointer",
                fontWeight: "bold"
              }}
              onClick={closeModal}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

