import './App.css';

import React, {useState} from 'react';
import axios from 'axios';


// Style for centering and customizing the look
const styles = {
  container: {
    textAlign: 'center',
    fontFamily: 'Arial, sans-serif',
    marginTop: '50px',
  },
  heading: {
    fontSize: '36px',
    fontWeight: 'bold',
    color: '#1d3b73', // Custom color for the heading
    fontFamily: 'Georgia, serif', // A nice serif font for the team name
  },
  subHeading: {
    fontSize: '24px',
    color: '#555',
    marginTop: '10px',
  },
  searchContainer: {
    marginTop: '20px',
  },
  input: {
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    width: '300px',
  },
  button: {
    marginLeft: '10px',
    padding: '10px 20px',
    fontSize: '16px',
    backgroundColor: '#007BFF',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  results: {
    marginTop: '20px',
    fontSize: '18px',
    fontWeight: 'bold',
  },
};

function App() {

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [playerStats, setPlayerStats] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/api/matching_players?first_name=${firstName}&last_name=${lastName}`);
      setPlayerStats(response.data);
    } catch (error) {
      console.error('Error fetching player data:', error);
      setPlayerStats(null);
    }
  };



  return (
    <div style={styles.container}>
      <div style={styles.heading}>Kansas City Royals</div>
      <div style={styles.subHeading}>Player Stat Lookup</div>

      <div style={styles.searchContainer}>
        <input
          type="text"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          placeholder="First Name"
          style={styles.input}
        />
        <input
          type="text"
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
          placeholder="Last Name"
          style={styles.input}
        />
        <button onClick={handleSearch} style={styles.button}>Search</button>
      </div>

      {playerStats && (
        <div style={styles.results}>
          <h3>Player Stats:</h3>
          {playerStats.length > 0 ? (
            <ul>
              {playerStats.map((player, index) => (
                <li key={index}>
                  <p>Name: {player.first_name} {player.last_name}</p>
                  <p>Height: {player.height} cm</p>
                  <p>Weight: {player.weight} kg</p>
                </li>
              ))}
            </ul>
          ) : (
            <p>No matching players found.</p>
          )}
                  </div>
                )}
    </div>
  );
}

// Then do some line in between each one and then view baseball stats and pitching stats
//Also have Each player left aligned, then their stats, then buttons to view their batting and pitching stats

export default App;
